import frameImage from "../../../assets/Images/frame.png";
import SignupForm from "./SignupForm";
import LoginForm from "./LoginForm";
import { FcGoogle } from "react-icons/fc";
import { useSelector } from "react-redux";

const Template = ({ title, desc1, desc2, image, formType }) => {
  // console.log("ye rha mera form type");
  // console.log(formtype)
  const { loading } = useSelector((state) => state.auth);
  return (
    <div>
      {loading ? (
        <div className=" min-h-screen flex justify-center items-center">
          <div className="spinner "></div>
        </div>
      ) : (
        <div className="flex justify-between w-11/12 max-w-[1160px] py-12 mx-auto gap-x-12 gap-y-0">
          <div className="w-11/12 max-w-[450px]">
            <h1 className="text-richblack-5 font-semibold text-[1.875rem] leading-[2.375rem]">
              {title}
            </h1>

            <p className="text-[1.125rem] leading[1.625rem] mt-4">
              <span className="text-richblack-100">{desc1}</span>
              <br />
              <span className="text-blue-100 italic">{desc2}</span>
            </p>

            {formType === "signup" ? <SignupForm /> : <LoginForm />}

            <div className="flex w-full items-center my-4 gap-x-2">
              <div className="w-full h-[1px] bg-richblack-700"></div>
              <p className="text-richblack-700 font-medium leading[1.375rem]">
                OR
              </p>
              <div className="w-full h-[1px] bg-richblack-700"></div>
            </div>

            <button
              className="w-full flex justify-center items-center rounded-[8px] font-medium text-richblack-100
            border border-richblack-700 px-[12px] py-[8px] gap-x-2 mt-6 "
            >
              <FcGoogle />
              <p className=" text-white">Sign Up with Google</p>
            </button>
          </div>

          <div className="flex items-center justify-center relative w-11/12 max-w-[450px] ">
            <img
              src={frameImage}
              alt="Pattern"
              width={558}
              height={504}
              loading="lazy"
            />

            <img
              src={image}
              alt="Students"
              width={558}
              height={490}
              loading="lazy"
              className="absolute top-[16%] right-[6%]"
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default Template;
